            .img-event{
                background-image: url("http://www.gskristallijn.be/wp-content/uploads/2015/08/slide-ice-skating-1160x400.jpg");
                filter: blur(30px);
               width: 110%;
               height: 200px;
               object-fit: cover;
                object-fit: cover;
                z-index: -1;
                position: absolute;
            }
        
    
        img{
            width: 30%;
            object-position: center;
            object-fit: cover;
        }
        &__info{
            padding: 1em 2em;
            width: 70%;
            position: relative;
        }
    
    
    }
}

