        h4,
        .left {
            margin-left: 2em;
        }

        .right {
            margin-right: 2em;
        }
    }
}


// _____________________________________________________________

.warning-sending-location {
    @extend %popup;
    background-color: rgba(172, 255, 47, 0);
    text-align: center;
    max-width: 500px;

    .warning {
        background-color: map.get($map: vars.$colors, $key: warning-color);
        border-radius: 10px 10px 0 0;

        p {
            padding: 2em;
            margin-bottom: 0;
        }
    }

    .deactivate {
        // height: 50px;
        border-radius: 0 0 10px 10px;
        background-color: map.get($map: vars.$colors, $key: popup-background-color);

        p {
            margin: 0;
            padding: 1em;
        }

        &:hover {
            background-color: rgba(164, 131, 255, 0.986);
            transition: 0.5s;
            cursor: pointer;
        }
    }
}

// ___________________________________________________________________

.warning-top {
    background-color: map.get($map: vars.$colors, $key: warning-color);
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 3;

    p {
        padding: 2em;
        font-weight: bold;
        text-align: center;
        max-width: 700px;
        margin: 0 auto;
    }
}

// ______________________________________________________________

.directions-vehicles-choice {
    background-color: map.get($map: vars.$colors, $key: directions-color);
    text-align: center;
    padding: 2em;
    max-width: 500px;
    margin: 0 auto;
    border-radius: 20px;
    @extend %popup;

    h3 {
        text-transform: uppercase;
    }

    .vehicles {
        display: flex;
        justify-content: space-between;
        max-width: 250px;
        margin: 3em auto 0 auto;

        i {
            font-size: 24px;

            &:hover {
                cursor: pointer;
                color: map.get($map: vars.$colors, $key: selected);
                transition: 0.2s;
            }
        }
    }
}


// __________________________________________________________

.route-duration {
    background-color: map.get($map: vars.$colors, $key: directions-color);
    text-align: center;
    padding: 1em;
    position: fixed;
    width: 100%;
    bottom: 6em;
    z-index: 4;
    

    @include media.for-tablet-portrait-up {
        bottom: 0;
    }

    .close {
        position: absolute;
        right: 2em;
        top: 0.5em;
    }
}

// _______________________________________________________________

.directions {
    background-color: map.get($map: vars.$colors, $key: directions-color);
    position: fixed;
    padding: 1em 2em;
    border-radius: 20px;
    top: 0;
    width: 90%;
    max-width: 500px;
    margin: 1em auto 0 auto;
    border: 1px solid map.get($map: vars.$colors, $key: white);
    z-index: 4;

    div {
        display: flex;
        align-items: center;
        max-width: 400px;
        margin: 0 auto;

        img {
            width: 15%;
        }
    
        .text {
            width: 100%;
            margin-left: 2em;
            font-weight: bold;
        }
    }

    
}

